import React, { useState, useEffect } from 'react'
import ListaCategorias from '../components/ListaCategorias'
import ListaPost from '../components/ListaPost'
import '../assets/css/blog.css'
import { Route, useParams, useRouteMatch, Link, Switch } from 'react-router-dom'
import { busca } from '../api/api'
import SubCategoria from './SubCategoria'


const Categoria = () => {
  const { id } = useParams()
  const { url, path } = useRouteMatch()
  const [subcategorias, setSubCategorias] = useState([])
  const [posts, setPosts] = useState([])

  useEffect(() => {
    busca(`movie/category/${id}`, (categoria) => {
      setSubCategorias(categoria.subcategorias)
      busca(url, setPosts)
    })
  }, [id])
  return (
    <>
      <div className="container">
        <h2 className="titulo-pagina">Movies by category:</h2>
      </div>
      <ListaCategorias />
      <ul className="lista-categorias container flex">
        {
          subcategorias.map((subcategoria) => (
            <li
              className={`lista-categorias__categoria lista-categorias__categoria--${id}`}
              key={subcategoria}
            >
              <Link to={`${url}/${subcategoria}`}>
                {subcategoria}
              </Link>
            </li>
          ))
        }
        <main className="container flex flex--centro">
      <article className="cartao post">
        <h2 className="cartao__titulo">{url.title}</h2>
        <h2 className="carta__titulo">{url.category}</h2>
        <p className="carta__texto">{url.description}</p>
        <h2 className="cartao__titulo">{url.year}</h2>
        <h2 className="cartao__titulo">{url.rating}</h2>
      </article>
    </main>
      </ul>
      <Switch>
        <Route exact path={`${path}/`}>
          <ListaPost url={`/category/?category=${id}`} />
        </Route>
        <Route path={`${path}/category/:category`}>
          <SubCategoria />
        </Route>
      </Switch>
    </>
  )
}
export default Categoria
